    InstagramButton: InstagramButton,
    NavBar: NavBar // Register the NavBar component
  },
  setup: function setup() {
    var state = reactive({
      isLogoSmall: false,
      showNavBar: false
    });
    var toggleLogoSize = function toggleLogoSize() {
      state.isLogoSmall = !state.isLogoSmall;
      state.showNavBar = state.isLogoSmall; // Show NavBar when the logo is small
    };
    var logoStyle = computed(function () {
